module.exports = {
    a: function (data) {
        let y = data.getFullYear();
        let m = data.getMonth() + 1;
        let d = data.getDate();
        let h = data.getHours();
        let i = data.getMinutes();
        let s = data.getSeconds();
        function addzero(a) {
            return a = a < 10 ? ('0' + a) : a;
        }
        return y + '-' + addzero(m) + '-' + addzero(d) + ' ' + addzero(h) + ':' + addzero(i) + ':' + addzero(s)
    }
};

